import './MealItemForm.css';
import Input from '..UI/Input';


function MealItemForm(props){

    return(
        <from className="form">
            <Input label="Amount" input={{
                id: 'amount',
                type: 'number',
                min: '1',
                max: '5',
                defaultValue: '1'
            }}>
            </Input>
            <button className="button-item">+ Add</button>
        </from>
    );
}

export default MealItemForm;
